// Add custom class to body tag if shortcode is present
function add_custom_body_class($classes)
{
  // Check if the current page/post content contains the shortcode
  if (is_singular() && has_shortcode(get_post()->post_content, 'dwk_imagemaping')) {
    $classes[] = 'dwk-image-maping-present';
  }
  return $classes;
}
add_filter('body_class', 'add_custom_body_class');
